import React from 'react';
import { Task, TaskCategory } from '../contexts/TaskContext';
import { useTask } from '../contexts/TaskContext';
import { FiCalendar, FiTag, FiCheck } from 'react-icons/fi';

interface TaskItemProps {
  task: Task;
}

const TaskItem: React.FC<TaskItemProps> = ({ task }) => {
  const { completeTask } = useTask();
  
  const getCategoryColor = (category: TaskCategory) => {
    switch (category) {
      case 'Personal':
        return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
      case 'Work':
        return 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200';
      case 'Home':
        return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      case 'Friends':
        return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200';
      case 'Family':
        return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200';
      default:
        return 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
    }
  };
  
  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  };
  
  const handleComplete = (e: React.MouseEvent) => {
    e.stopPropagation();
    completeTask(task.id);
  };
  
  return (
    <div className="flex items-center p-3 bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-100 dark:border-gray-700">
      <button 
        onClick={handleComplete}
        className="w-6 h-6 rounded-full border-2 border-primary-light dark:border-primary-dark flex items-center justify-center mr-3 hover:bg-primary-light hover:dark:bg-primary-dark hover:text-white transition-colors"
      >
        <FiCheck className="text-sm opacity-0 hover:opacity-100" />
      </button>
      
      <div className="flex-1">
        <h3 className="font-medium">{task.title}</h3>
        {task.description && (
          <p className="text-sm text-gray-600 dark:text-gray-400 mt-1">{task.description}</p>
        )}
        
        <div className="flex items-center mt-2 text-xs">
          <div className="flex items-center mr-3 text-gray-600 dark:text-gray-400">
            <FiCalendar className="mr-1" />
            {formatDate(task.dueDate)}
          </div>
          
          <div className={`px-2 py-0.5 rounded-full ${getCategoryColor(task.category)}`}>
            <div className="flex items-center">
              <FiTag className="mr-1" />
              {task.category}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TaskItem;
