import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import { FiMoon, FiSun, FiLogOut, FiAward, FiCalendar, FiCheckCircle, FiFlag, FiHelpCircle } from 'react-icons/fi';
import { useTour } from '../contexts/TourContext';
import { motion } from 'framer-motion';

type ThemeColor = 'indigo' | 'emerald' | 'rose' | 'amber' | 'blue';

const Profile: React.FC = () => {
  const { user, updateProfile, logout } = useAuth();
  const { mode, toggleMode, themeColor, setThemeColor } = useTheme();
  const { startTour } = useTour();
  
  const [fullName, setFullName] = useState(user?.fullName || '');
  const [city, setCity] = useState(user?.location?.city || '');
  const [country, setCountry] = useState(user?.location?.country || '');
  const [workweekDays, setWorkweekDays] = useState<number[]>(user?.workweekDays || [1, 2, 3, 4, 5]);
  const [isEditing, setIsEditing] = useState(!user?.fullName);
  
  const themeColors: ThemeColor[] = ['indigo', 'emerald', 'rose', 'amber', 'blue'];
  const weekdays = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
  
  useEffect(() => {
    // If user just registered, prompt them to complete their profile
    if (user && !user.fullName) {
      setIsEditing(true);
    }
  }, [user]);
  
  const handleSaveProfile = () => {
    if (!fullName || !city || !country) {
      return;
    }
    
    updateProfile({
      fullName,
      location: {
        city,
        country,
      },
      workweekDays,
    });
    
    setIsEditing(false);
  };
  
  const toggleWorkday = (day: number) => {
    if (workweekDays.includes(day)) {
      setWorkweekDays(workweekDays.filter(d => d !== day));
    } else {
      setWorkweekDays([...workweekDays, day].sort());
    }
  };
  
  const getCountryFlag = (countryName: string) => {
    // This is a simplified version. In a real app, you would use a proper country code lookup
    const countryCodeMap: Record<string, string> = {
      'USA': '🇺🇸',
      'Canada': '🇨🇦',
      'UK': '🇬🇧',
      'Australia': '🇦🇺',
      'Germany': '🇩🇪',
      'France': '🇫🇷',
      'Japan': '🇯🇵',
      'China': '🇨🇳',
      'India': '🇮🇳',
      'Brazil': '🇧🇷',
    };
    
    return countryCodeMap[countryName] || '🌍';
  };
  
  const formatJoinDate = (dateString: string) => {
    const joinDate = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now.getTime() - joinDate.getTime());
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    return diffDays;
  };
  
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { type: "spring", stiffness: 300, damping: 24 }
    }
  };

  if (!user) return null;
  
  return (
    <motion.div 
      className="max-w-lg mx-auto"
      variants={containerVariants}
      initial="hidden"
      animate="visible"
    >
      <motion.div 
        className="card p-6 mb-6"
        variants={itemVariants}
      >
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold text-gray-900 dark:text-white">Profile</h2>
          {isEditing ? (
            <div className="flex space-x-2">
              <motion.button 
                onClick={() => setIsEditing(false)} 
                className="text-sm text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                Cancel
              </motion.button>
              <motion.button 
                onClick={handleSaveProfile} 
                className="text-sm bg-primary text-white px-3 py-1 rounded-md hover:bg-primary-dark"
                style={{ backgroundColor: 'var(--color-primary)' }}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                Save
              </motion.button>
            </div>
          ) : (
            <motion.button 
              onClick={() => setIsEditing(true)} 
              className="text-sm text-primary hover:text-primary-dark"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Edit
            </motion.button>
          )}
        </div>
        
        {isEditing ? (
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                Full Name
              </label>
              <input
                type="text"
                value={fullName}
                onChange={(e) => setFullName(e.target.value)}
                className="input w-full"
                placeholder="Your full name"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                City
              </label>
              <input
                type="text"
                value={city}
                onChange={(e) => setCity(e.target.value)}
                className="input w-full"
                placeholder="Your city"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                Country
              </label>
              <input
                type="text"
                value={country}
                onChange={(e) => setCountry(e.target.value)}
                className="input w-full"
                placeholder="Your country"
              />
            </div>
          </div>
        ) : (
          <div>
            <h3 className="text-2xl font-bold text-gray-900 dark:text-white mb-2">
              {user.fullName || user.username}
            </h3>
            {user.location && (user.location.city || user.location.country) && (
              <p className="text-gray-600 dark:text-gray-400 flex items-center">
                <span className="mr-1">{getCountryFlag(user.location.country)}</span>
                {[user.location.city, user.location.country].filter(Boolean).join(', ')}
              </p>
            )}
          </div>
        )}
      </motion.div>
      
      <motion.div 
        className="card p-6 mb-6"
        variants={itemVariants}
      >
        <h2 className="text-xl font-bold text-gray-900 dark:text-white mb-4">Stats</h2>
        <div className="grid grid-cols-2 gap-4">
          <div className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg">
            <p className="text-sm text-gray-500 dark:text-gray-400">Current Streak</p>
            <p className="text-2xl font-bold text-gray-900 dark:text-white">{user.streak} days</p>
          </div>
          <div className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg">
            <p className="text-sm text-gray-500 dark:text-gray-400">Tasks Completed</p>
            <p className="text-2xl font-bold text-gray-900 dark:text-white">{user.tasksCompleted}</p>
          </div>
          <div className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg">
            <p className="text-sm text-gray-500 dark:text-gray-400">Achievements</p>
            <p className="text-2xl font-bold text-gray-900 dark:text-white">{user.achievements}</p>
          </div>
          <div className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg">
            <p className="text-sm text-gray-500 dark:text-gray-400">Member Since</p>
            <p className="text-lg font-bold text-gray-900 dark:text-white">{formatJoinDate(user.joinDate)}</p>
          </div>
        </div>
      </motion.div>
      
      <motion.div 
        className="card p-6 mb-6"
        variants={itemVariants}
      >
        <h2 className="text-xl font-bold text-gray-900 dark:text-white mb-4">Settings</h2>
        
        <div className="mb-6">
          <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-3">Theme Mode</h3>
          <motion.button 
            onClick={toggleMode}
            className="flex items-center justify-center w-12 h-12 rounded-full bg-gray-100 dark:bg-gray-700 text-gray-700 dark:text-gray-300"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            {mode === 'dark' ? <FiSun size={24} /> : <FiMoon size={24} />}
          </motion.button>
        </div>
        
        <div className="mb-6">
          <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-3">Theme Color</h3>
          <div className="flex space-x-3">
            {themeColors.map((color) => (
              <motion.button
                key={color}
                onClick={() => setThemeColor(color)}
                className={`w-8 h-8 rounded-full ${
                  themeColor === color ? 'ring-2 ring-offset-2 ring-offset-white dark:ring-offset-gray-800 ring-gray-400' : ''
                }`}
                style={{ backgroundColor: `var(--color-${color})` }}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              />
            ))}
          </div>
        </div>
        
        <div>
          <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-3">Workweek Days</h3>
          <div className="flex flex-wrap gap-2">
            {weekdays.map((day, index) => (
              <motion.button
                key={day}
                onClick={() => toggleWorkday(index + 1)}
                className={`w-10 h-10 rounded-full flex items-center justify-center ${
                  workweekDays.includes(index + 1)
                    ? 'bg-primary text-white'
                    : 'bg-gray-100 dark:bg-gray-700 text-gray-700 dark:text-gray-300'
                }`}
                style={workweekDays.includes(index + 1) ? { backgroundColor: 'var(--color-primary)' } : {}}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                {day.substring(0, 1)}
              </motion.button>
            ))}
          </div>
        </div>
      </motion.div>

      <motion.div 
        className="card p-6 mb-6"
        variants={itemVariants}
      >
        <h2 className="text-xl font-bold text-gray-900 dark:text-white mb-4">Help</h2>
        <motion.button
          onClick={startTour}
          className="flex items-center text-primary hover:text-primary-dark"
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
        >
          <FiHelpCircle className="mr-2" size={20} />
          <span>Restart Guided Tour</span>
        </motion.button>
      </motion.div>

      <motion.div 
        className="card p-6 mb-6"
        variants={itemVariants}
      >
        <button 
          onClick={logout}
          className="btn w-full flex items-center justify-center bg-red-500 hover:bg-red-600 text-white"
        >
          <FiLogOut className="mr-2" /> Logout
        </button>
      </motion.div>
    </motion.div>
  );
};

export default Profile;
