import React from 'react';
import { useAuth } from '../contexts/AuthContext';
import { FiAward, FiTrendingUp, FiTarget } from 'react-icons/fi';

const Stats: React.FC = () => {
  const { user } = useAuth();
  
  if (!user) return null;
  
  // Calculate progress percentages
  const streakProgress = Math.min(user.streak / 7 * 100, 100); // 7 days streak = 100%
  const tasksProgress = user.tasksCompleted % 5 / 5 * 100; // Progress to next achievement
  
  // Calculate next achievement threshold
  const getNextAchievementThreshold = () => {
    const base = 5;
    const current = user.achievements;
    return base * Math.pow(2, current);
  };
  
  const nextAchievement = getNextAchievementThreshold();
  const achievementProgress = (user.tasksCompleted % nextAchievement) / nextAchievement * 100;
  
  // Helper function to create circular progress
  const CircularProgress = ({ 
    percentage, 
    size = 160, 
    strokeWidth = 15, 
    color = 'var(--color-primary-light)',
    darkColor = 'var(--color-primary-dark)',
    icon,
    label,
    value,
  }: {
    percentage: number;
    size?: number;
    strokeWidth?: number;
    color?: string;
    darkColor?: string;
    icon: React.ReactNode;
    label: string;
    value: string | number;
  }) => {
    const radius = (size - strokeWidth) / 2;
    const circumference = radius * 2 * Math.PI;
    const dash = (percentage * circumference) / 100;
    
    return (
      <div className="flex flex-col items-center">
        <div className="relative" style={{ width: size, height: size }}>
          {/* Background circle */}
          <svg width={size} height={size} className="rotate-[-90deg]">
            <circle
              cx={size / 2}
              cy={size / 2}
              r={radius}
              fill="transparent"
              stroke="currentColor"
              strokeWidth={strokeWidth}
              className="text-gray-200 dark:text-gray-700"
            />
            
            {/* Progress circle */}
            <circle
              cx={size / 2}
              cy={size / 2}
              r={radius}
              fill="transparent"
              stroke="currentColor"
              strokeWidth={strokeWidth}
              strokeDasharray={circumference}
              strokeDashoffset={circumference - dash}
              className="text-primary-light dark:text-primary-dark"
              strokeLinecap="round"
            />
          </svg>
          
          {/* Center content */}
          <div className="absolute inset-0 flex flex-col items-center justify-center">
            <div className="text-3xl text-primary-light dark:text-primary-dark mb-1">
              {icon}
            </div>
            <div className="text-2xl font-bold">{value}</div>
          </div>
        </div>
        <div className="mt-3 text-center">
          <div className="text-lg font-medium">{label}</div>
          <div className="text-sm text-gray-600 dark:text-gray-400">{Math.round(percentage)}% complete</div>
        </div>
      </div>
    );
  };
  
  return (
    <div className="pb-16 md:pb-0">
      <h1 className="text-2xl font-bold mb-6">Stats</h1>
      
      <div className="card">
        <h2 className="text-xl font-semibold mb-6">Your Progress</h2>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 py-4">
          <CircularProgress
            percentage={tasksProgress}
            icon={<FiTarget />}
            label="Tasks Completed"
            value={user.tasksCompleted}
          />
          
          <CircularProgress
            percentage={streakProgress}
            icon={<FiTrendingUp />}
            label="Current Streak"
            value={user.streak}
            color="var(--color-secondary-light)"
            darkColor="var(--color-secondary-dark)"
          />
          
          <CircularProgress
            percentage={achievementProgress}
            icon={<FiAward />}
            label="Next Achievement"
            value={`${user.achievements + 1}`}
            color="#f59e0b"
            darkColor="#d97706"
          />
        </div>
      </div>
      
      <div className="card mt-6">
        <h2 className="text-xl font-semibold mb-4">Achievements</h2>
        
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-5 gap-4">
          {Array.from({ length: 10 }).map((_, index) => {
            const isEarned = index < user.achievements;
            const threshold = 5 * Math.pow(2, index);
            
            return (
              <div 
                key={index}
                className={`p-4 rounded-lg text-center ${
                  isEarned 
                    ? 'bg-amber-100 dark:bg-amber-900 text-amber-800 dark:text-amber-200' 
                    : 'bg-gray-100 dark:bg-gray-800 text-gray-400 dark:text-gray-600'
                }`}
              >
                <div className={`text-3xl mb-2 ${isEarned ? 'text-amber-500' : 'text-gray-400'}`}>
                  🏅
                </div>
                <div className="font-medium">Level {index + 1}</div>
                <div className="text-xs mt-1">{threshold} tasks</div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Stats;
