import React, { useState } from 'react';
import { FiMoon, FiSun, FiLogOut, FiAward, FiCalendar, FiCheckCircle, FiFlag, FiHelpCircle } from 'react-icons/fi';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import { useTour } from '../contexts/TourContext';

type ThemeColor = 'indigo' | 'emerald' | 'rose' | 'amber' | 'blue';

const Profile: React.FC = () => {
  const { user, updateProfile, logout } = useAuth();
  const { mode, toggleMode, themeColor, setThemeColor } = useTheme();
  const { startTour } = useTour();

  const [fullName, setFullName] = useState(user?.fullName || '');
  const [city, setCity] = useState(user?.location?.city || '');
  const [country, setCountry] = useState(user?.location?.country || '');
  const [isEditing, setIsEditing] = useState(false);

  const themeColors: ThemeColor[] = ['indigo', 'emerald', 'rose', 'amber', 'blue'];
  const weekdays = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];

  const handleSaveProfile = () => {
    updateProfile({
      fullName,
      location: {
        city,
        country
      }
    });
    setIsEditing(false);
  };

  const toggleWorkday = (day: number) => {
    if (!user) return;

    const updatedWorkdays = user.workweekDays.includes(day)
      ? user.workweekDays.filter(d => d !== day)
      : [...user.workweekDays, day];

    updateProfile({
      workweekDays: updatedWorkdays
    });
  };

  const getCountryFlag = (countryName: string) => {
    // This is a simplified version - in a real app, you'd use a proper country code lookup
    const countryMap: Record<string, string> = {
      'USA': '🇺🇸',
      'UK': '🇬🇧',
      'Canada': '🇨🇦',
      'Australia': '🇦🇺',
      'Germany': '🇩🇪',
      'France': '🇫🇷',
      'Japan': '🇯🇵',
      'China': '🇨🇳',
      'India': '🇮🇳',
      'Brazil': '🇧🇷',
    };

    return countryMap[countryName] || '🌍';
  };

  const formatJoinDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    });
  };

  if (!user) return null;

  return (
    <div className="pb-16 md:pb-0">
      <h1 className="text-2xl font-bold mb-6">Profile</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Profile Information */}
        <div className="card md:col-span-2">
          <div className="flex flex-col items-center mb-6">
            <div className="w-24 h-24 rounded-full bg-primary-light dark:bg-primary-dark text-white flex items-center justify-center text-4xl mb-4">
              {user.fullName ? user.fullName.charAt(0).toUpperCase() : user.username.charAt(0).toUpperCase()}
            </div>

            {!isEditing && (
              <>
                <h2 className="text-xl font-semibold">{user.fullName || user.username}</h2>
                {user.location && (
                  <p className="text-gray-600 dark:text-gray-400 mt-1">
                    {user.location.city}, {user.location.country} {getCountryFlag(user.location.country)}
                  </p>
                )}
                <button 
                  className="mt-4 btn btn-secondary"
                  onClick={() => setIsEditing(true)} 
                >
                  Edit Profile
                </button>
              </>
            )}

            {isEditing && (
              <div className="w-full max-w-md mt-4">
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                    Full Name
                  </label>
                  <input
                    type="text"
                    className="input"
                    value={fullName}
                    onChange={(e) => setFullName(e.target.value)}
                    placeholder="Enter your full name"
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                    City
                  </label>
                  <input
                    type="text"
                    className="input"
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    placeholder="Enter your city"
                  />
                </div>

                <div className="mb-6">
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                    Country
                  </label>
                  <input
                    type="text"
                    className="input"
                    value={country}
                    onChange={(e) => setCountry(e.target.value)}
                    placeholder="Enter your country"
                  />
                </div>

                <button 
                  className="btn btn-primary w-full"
                  onClick={handleSaveProfile}
                  disabled={!fullName || !city || !country}
                >
                  Save Profile
                </button>
              </div>
            )}
          </div>

          {/* Redesigned Statistics Section */}
          <div className="mt-8 relative">
            <div className="absolute inset-0 bg-gradient-to-r from-primary-light/10 to-primary-dark/10 dark:from-primary-light/5 dark:to-primary-dark/5 rounded-xl"></div>

            <div className="relative grid grid-cols-2 md:grid-cols-4 gap-0 divide-x divide-gray-200 dark:divide-gray-700 rounded-xl overflow-hidden">
              {/* Streak */}
              <div className="p-6 flex flex-col items-center justify-center transition-all hover:bg-white/50 dark:hover:bg-gray-800/50">
                <div className="w-12 h-12 rounded-full bg-gradient-to-br from-amber-400 to-red-500 flex items-center justify-center mb-3 shadow-lg transform transition-transform hover:scale-110">
                  <FiFlag className="text-white text-xl" />
                </div>
                <div className="text-3xl font-bold mb-1">{user.streak}</div>
                <div className="text-sm text-gray-600 dark:text-gray-400 font-medium">Day Streak</div>
              </div>

              {/* Achievements */}
              <div className="p-6 flex flex-col items-center justify-center transition-all hover:bg-white/50 dark:hover:bg-gray-800/50">
                <div className="w-12 h-12 rounded-full bg-gradient-to-br from-blue-400 to-indigo-600 flex items-center justify-center mb-3 shadow-lg transform transition-transform hover:scale-110">
                  <FiAward className="text-white text-xl" />
                </div>
                <div className="text-3xl font-bold mb-1">{user.achievements}</div>
                <div className="text-sm text-gray-600 dark:text-gray-400 font-medium">Achievements</div>
              </div>

              {/* Member Since */}
              <div className="p-6 flex flex-col items-center justify-center transition-all hover:bg-white/50 dark:hover:bg-gray-800/50">
                <div className="w-12 h-12 rounded-full bg-gradient-to-br from-emerald-400 to-green-600 flex items-center justify-center mb-3 shadow-lg transform transition-transform hover:scale-110">
                  <FiCalendar className="text-white text-xl" />
                </div>
                <div className="text-sm font-bold mb-1">
                  {new Date(user.joinDate).toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' })}
                </div>
                <div className="text-sm text-gray-600 dark:text-gray-400 font-medium">Member Since</div>
              </div>

              {/* Tasks Completed */}
              <div className="p-6 flex flex-col items-center justify-center transition-all hover:bg-white/50 dark:hover:bg-gray-800/50">
                <div className="w-12 h-12 rounded-full bg-gradient-to-br from-rose-400 to-pink-600 flex items-center justify-center mb-3 shadow-lg transform transition-transform hover:scale-110">
                  <FiCheckCircle className="text-white text-xl" />
                </div>
                <div className="text-3xl font-bold mb-1">{user.tasksCompleted}</div>
                <div className="text-sm text-gray-600 dark:text-gray-400 font-medium">Tasks Completed</div>
              </div>
            </div>

            {/* Achievement Progress */}
            {user.tasksCompleted > 0 && (
              <div className="mt-6 p-4 bg-white dark:bg-gray-800 rounded-lg shadow-sm">
                <div className="flex justify-between items-center mb-2">
                  <h3 className="text-sm font-medium">Next Achievement Progress</h3>
                  <span className="text-xs text-primary-light dark:text-primary-dark font-medium">
                    Level {user.achievements + 1}
                  </span>
                </div>

                {/* Calculate next achievement threshold */}
                {(() => {
                  const nextThreshold = 5 * Math.pow(2, user.achievements);
                  const progress = Math.min(100, (user.tasksCompleted / nextThreshold) * 100);

                  return (
                    <>
                      <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                        <div 
                          className="bg-primary-light dark:bg-primary-dark h-2 rounded-full transition-all duration-500 ease-out"
                          style={{ width: `${progress}%` }}
                        ></div>
                      </div>
                      <div className="flex justify-between mt-1">
                        <span className="text-xs text-gray-500 dark:text-gray-400">
                          {user.tasksCompleted} tasks
                        </span>
                        <span className="text-xs text-gray-500 dark:text-gray-400">
                          {nextThreshold} tasks
                        </span>
                      </div>
                    </>
                  );
                })()}
              </div>
            )}
          </div>
        </div>

        {/* Settings */}
        <div className="card">
          <h2 className="text-xl font-semibold mb-4">Settings</h2>

          <div className="mb-6">
            <div className="flex items-center justify-between mb-2">
              <span className="text-gray-700 dark:text-gray-300">Theme Mode</span>
              <button 
                onClick={toggleMode}
                className="p-2 rounded-full bg-gray-200 dark:bg-gray-700"
              >
                {mode === 'light' ? <FiMoon className="text-gray-700" /> : <FiSun className="text-yellow-400" />}
              </button>
            </div>
            <div className="text-sm text-gray-600 dark:text-gray-400">
              {mode === 'light' ? 'Light mode is active' : 'Dark mode is active'}
            </div>
          </div>

          <div className="mb-6">
            <div className="mb-2">
              <span className="text-gray-700 dark:text-gray-300">Theme Color</span>
            </div>
            <div className="flex space-x-2">
              {themeColors.map((color) => (
                <button
                  key={color}
                  onClick={() => setThemeColor(color)}
                  className={`w-8 h-8 rounded-full ${
                    themeColor === color ? 'ring-2 ring-offset-2 ring-gray-400 dark:ring-gray-600' : ''
                  }`}
                  style={{ 
                    backgroundColor: 
                      color === 'indigo' ? '#4f46e5' : 
                      color === 'emerald' ? '#10b981' : 
                      color === 'rose' ? '#e11d48' : 
                      color === 'amber' ? '#d97706' : 
                      '#2563eb'
                  }}
                />
              ))}
            </div>
          </div>

          <div className="mb-6">
            <div className="mb-2">
              <span className="text-gray-700 dark:text-gray-300">Workweek Days</span>
            </div>
            <div className="grid grid-cols-7 gap-1">
              {weekdays.map((day, index) => {
                const dayNumber = index + 1;
                return (
                  <button
                    key={day}
                    onClick={() => toggleWorkday(dayNumber)}
                    className={`text-xs p-1 rounded ${
                      user.workweekDays.includes(dayNumber)
                        ? 'bg-primary-light dark:bg-primary-dark text-white'
                        : 'bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300'
                    }`}
                  >
                    {day.substring(0, 1)}
                  </button>
                );
              })}
            </div>
            <div className="text-sm text-gray-600 dark:text-gray-400 mt-2">
              Select your working days
            </div>
          </div>

          <div className="mb-6">
            <div className="mb-2">
              <span className="text-gray-700 dark:text-gray-300">Help</span>
            </div>
            <button 
              onClick={startTour}
              className="flex items-center text-primary-light dark:text-primary-dark hover:underline"
            >
              <FiHelpCircle className="mr-2" />
              Start Guided Tour
            </button>
          </div>

          <button 
            onClick={logout}
            className="btn btn-secondary w-full flex items-center justify-center"
          >
            <FiLogOut className="mr-2" /> Logout
          </button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
