import React from 'react';
import { Outlet, NavLink, useLocation } from 'react-router-dom';
import { FiCalendar, FiCheckSquare, FiBarChart2, FiUser } from 'react-icons/fi';

const Layout: React.FC = () => {
  const location = useLocation();
  
  const navItems = [
    { path: '/plannr', label: 'Plannr', icon: <FiCalendar /> },
    { path: '/tasks', label: 'Tasks', icon: <FiCheckSquare /> },
    { path: '/stats', label: 'Stats', icon: <FiBarChart2 /> },
    { path: '/profile', label: 'Profile', icon: <FiUser /> },
  ];

  return (
    <div className="flex h-screen bg-background-light dark:bg-background-dark">
      {/* Desktop Sidebar */}
      <div className="desktop-nav p-4">
        <div className="mb-8">
          <h1 className="text-2xl font-bold text-primary-light dark:text-primary-dark">Plannr</h1>
        </div>
        <nav className="flex flex-col space-y-2">
          {navItems.map((item) => (
            <NavLink
              key={item.path}
              to={item.path}
              className={({ isActive }) => 
                `nav-item ${isActive ? 'active' : ''}`
              }
            >
              <span className="mr-3 text-xl">{item.icon}</span>
              {item.label}
            </NavLink>
          ))}
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col md:ml-64 overflow-hidden">
        <main className="flex-1 overflow-y-auto p-4 pb-20 md:pb-4">
          <div className="container-app">
            <Outlet />
          </div>
        </main>

        {/* Mobile Bottom Navigation */}
        <nav className="mobile-nav">
          <div className="flex justify-around">
            {navItems.map((item) => (
              <NavLink
                key={item.path}
                to={item.path}
                className={({ isActive }) => 
                  `flex flex-col items-center py-3 px-2 ${
                    isActive 
                      ? 'text-primary-light dark:text-primary-dark' 
                      : 'text-gray-500 dark:text-gray-400'
                  }`
                }
              >
                <span className="text-xl">{item.icon}</span>
                <span className="text-xs mt-1">{item.label}</span>
              </NavLink>
            ))}
          </div>
        </nav>
      </div>
    </div>
  );
};

export default Layout;
