import React, { useState, useContext, useMemo } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { FiAward, FiTrendingUp, FiTarget, FiCalendar, FiBarChart2, FiClock, FiCheckCircle, FiPieChart, FiActivity, FiClipboard, FiCheckSquare, FiSun, FiMoon, FiZap, FiCoffee, FiSmile, FiHelpCircle, FiSunrise, FiSunset } from 'react-icons/fi';
import { motion, AnimatePresence } from 'framer-motion';
import { TaskContext, Task } from '../contexts/TaskContext';

const Stats: React.FC = () => {
  const { user } = useAuth();
  const { tasks } = useContext(TaskContext);

  if (!user) return null;

  // Calculate progress percentages
  const streakProgress = Math.min(user.streak / 7 * 100, 100); // 7 days streak = 100%
  const tasksProgress = user.tasksCompleted % 5 / 5 * 100; // Progress to next achievement

  // Calculate next achievement threshold
  const getNextAchievementThreshold = () => {
    const base = 5;
    const current = user.achievements;
    return base * Math.pow(2, current);
  };

  const nextAchievement = getNextAchievementThreshold();
  const achievementProgress = (user.tasksCompleted % nextAchievement) / nextAchievement * 100;

  // Helper function to create circular progress
  const CircularProgress = ({ 
    percentage, 
    size = 160, 
    strokeWidth = 8, 
    color = 'var(--color-primary-light)',
    darkColor = 'var(--color-primary-dark)',
    icon,
    label,
    value,
    delay = 0,
  }: {
    percentage: number;
    size?: number;
    strokeWidth?: number;
    color?: string;
    darkColor?: string;
    icon: React.ReactNode;
    label: string;
    value: string | number;
    delay?: number;
  }) => {
    const radius = (size - strokeWidth) / 2;
    const circumference = radius * 2 * Math.PI;
    const dash = (percentage * circumference) / 100;

    return (
      <motion.div 
        className="flex flex-col items-center"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay }}
      >
        <div className="relative" style={{ width: size, height: size }}>
          {/* Background circle */}
          <svg width={size} height={size} className="rotate-[-90deg]">
            <circle
              cx={size / 2}
              cy={size / 2}
              r={radius}
              fill="transparent"
              stroke="currentColor"
              strokeWidth={strokeWidth}
              className="text-gray-100 dark:text-gray-800"
            />

            {/* Progress circle */}
            <motion.circle
              cx={size / 2}
              cy={size / 2}
              r={radius}
              fill="transparent"
              stroke="currentColor"
              strokeWidth={strokeWidth}
              strokeDasharray={circumference}
              strokeDashoffset={circumference}
              className="text-primary-light dark:text-primary-dark"
              strokeLinecap="round"
              initial={{ strokeDashoffset: circumference }}
              animate={{ strokeDashoffset: circumference - dash }}
              transition={{ duration: 1.5, delay: delay + 0.2, ease: "easeOut" }}
            />
          </svg>

          {/* Center content */}
          <div className="absolute inset-0 flex flex-col items-center justify-center">
