import React from 'react';
import { FiCheck, FiClock, FiTag } from 'react-icons/fi';
import { Task, TaskCategory } from '../contexts/TaskContext';
import { useTask } from '../contexts/TaskContext';
import { motion } from 'framer-motion';

interface TaskItemProps {
  task: Task;
}

const TaskItem: React.FC<TaskItemProps> = ({ task }) => {
  const { completeTask } = useTask();
  
  const getCategoryColor = (category: TaskCategory) => {
    switch (category) {
      case 'Work':
        return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
      case 'Personal':
        return 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200';
      case 'Home':
        return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      case 'Friends':
        return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200';
      case 'Family':
        return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200';
      default:
        return 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
    }
  };
  
  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  };
  
  const handleComplete = (e: React.MouseEvent) => {
    e.stopPropagation();
    completeTask(task.id);
  };
  
  return (
    <motion.div 
      className={`card p-4 mb-3 rounded-lg ${task.completed ? 'opacity-70' : ''}`}
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, x: -10 }}
      whileHover={{ scale: 1.02 }}
      transition={{ duration: 0.2 }}
      layout
    >
      <div className="flex items-start">
        <motion.button
          className={`flex-shrink-0 w-6 h-6 rounded-full border-2 mr-3 flex items-center justify-center ${
            task.completed 
              ? 'bg-primary border-primary text-white' 
              : 'border-gray-300 dark:border-gray-600'
          }`}
          onClick={handleComplete}
          whileTap={{ scale: 0.9 }}
          whileHover={{ scale: 1.1 }}
        >
          {task.completed && <FiCheck size={14} />}
        </motion.button>
        
        <div className="flex-grow">
          <h3 className={`font-medium text-gray-900 dark:text-white ${task.completed ? 'line-through' : ''}`}>
            {task.title}
          </h3>
          
          {task.description && (
            <p className="text-sm text-gray-600 dark:text-gray-400 mt-1">
              {task.description}
            </p>
          )}
          
          <div className="flex flex-wrap items-center mt-2 text-xs">
            <motion.span 
              className={`inline-flex items-center px-2 py-1 rounded-full mr-2 mb-1 ${getCategoryColor(task.category)}`}
              whileHover={{ scale: 1.05 }}
            >
              <FiTag className="mr-1" size={12} />
              {task.category}
            </motion.span>
            
            <motion.span 
              className="inline-flex items-center text-gray-500 dark:text-gray-400 mb-1"
              whileHover={{ scale: 1.05 }}
            >
              <FiClock className="mr-1" size={12} />
              {formatDate(task.dueDate)}
            </motion.span>
          </div>
        </div>
      </div>
    </motion.div>
  );
};

export default TaskItem;
