import React, { useState } from 'react';
import { FiMoon, FiSun, FiLogOut, FiAward, FiCalendar, FiCheckCircle, FiFlag, FiHelpCircle } from 'react-icons/fi';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import { useTour } from '../contexts/TourContext';

type ThemeColor = 'indigo' | 'emerald' | 'rose' | 'amber' | 'blue';

const Profile: React.FC = () => {
  const { user, updateProfile, logout } = useAuth();
  const { mode, toggleMode, themeColor, setThemeColor } = useTheme();
  const { startTour } = useTour();

  const [fullName, setFullName] = useState(user?.fullName || '');
  const [city, setCity] = useState(user?.location?.city || '');
  const [country, setCountry] = useState(user?.location?.country || '');
  const [isEditing, setIsEditing] = useState(false);

  const themeColors: ThemeColor[] = ['indigo', 'emerald', 'rose', 'amber', 'blue'];
  const weekdays = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];

  const handleSaveProfile = () => {
    updateProfile({
      fullName,
      location: {
        city,
        country
      }
    });
    setIsEditing(false);
  };

  const toggleWorkday = (day: number) => {
    if (!user) return;

    const updatedWorkdays = user.workweekDays.includes(day)
      ? user.workweekDays.filter(d => d !== day)
      : [...user.workweekDays, day];

    updateProfile({
      workweekDays: updatedWorkdays
    });
  };

  const getCountryFlag = (countryName: string) => {
    // This is a simplified version - in a real app, you'd use a proper country code lookup
    const countryMap: Record<string, string> = {
      'USA': '🇺🇸',
      'UK': '🇬🇧',
      'Canada': '🇨🇦',
      'Australia': '🇦🇺',
      'Germany': '🇩🇪',
      'France': '🇫🇷',
      'Japan': '🇯🇵',
      'China': '🇨🇳',
      'India': '🇮🇳',
      'Brazil': '🇧🇷',
    };

    return countryMap[countryName] || '🌍';
  };

  const formatJoinDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    });
  };

  if (!user) return null;

  return (
    <div className="pb-16 md:pb-0">
      <h1 className="text-2xl font-bold mb-6">Profile</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Profile Information */}
        <div className="card md:col-span-2">
          <div className="flex flex-col items-center mb-6">
            {/* Redesigned Avatar */}
            <div className="relative mb-6">
              <div className="absolute -inset-0.5 bg-gradient-to-r from-primary-light to-primary-dark rounded-full blur opacity-75"></div>
              <div className="relative w-28 h-28 rounded-full bg-gradient-to-br from-primary-light to-primary-dark text-white flex items-center justify-center text-4xl font-bold shadow-xl">
                {user.fullName ? user.fullName.charAt(0).toUpperCase() : user.username.charAt(0).toUpperCase()}
              </div>
            </div>

            {!isEditing && (
              <div className="text-center">
                <h2 className="text-2xl font-bold mb-1">{user.fullName || user.username}</h2>
                {user.location && (
                  <div className="flex items-center justify-center space-x-1 mb-4">
                    <span className="text-lg text-gray-600 dark:text-gray-400">
                      {getCountryFlag(user.location.country)}
                    </span>
                    <p className="text-gray-600 dark:text-gray-400">
                      {user.location.city}, {user.location.country}
                    </p>
                  </div>
                )}
                <button 
                  className="mt-4 px-6 py-2 bg-transparent border border-gray-300 dark:border-gray-600 rounded-full text-sm font-medium text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors"
                  onClick={() => setIsEditing(true)} 
                >
                  Edit Profile
                </button>
              </div>
            )}

            {isEditing && (
              <div className="w-full max-w-md mt-4">
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                    Full Name
                  </label>
                  <input
                    type="text"
                    className="w-full px-4 py-2 rounded-lg border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-primary-light dark:focus:ring-primary-dark"
                    value={fullName}
                    onChange={(e) => setFullName(e.target.value)}
                    placeholder="Enter your full name"
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                    City
                  </label>
                  <input
                    type="text"
                    className="w-full px-4 py-2 rounded-lg border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-primary-light dark:focus:ring-primary-dark"
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    placeholder="Enter your city"
                  />
                </div>

                <div className="mb-6">
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                    Country
                  </label>
                  <input
                    type="text"
                    className="w-full px-4 py-2 rounded-lg border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-primary-light dark:focus:ring-primary-dark"
                    value={country}
                    onChange={(e) => setCountry(e.target.value)}
                    placeholder="Enter your country"
                  />
                </div>

                <div className="flex space-x-3">
                  <button 
                    className="flex-1 py-2 rounded-lg bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 font-medium"
                    onClick={() => setIsEditing(false)}
                  >
                    Cancel
                  </button>
                  <button 
                    className="flex-1 py-2 rounded-lg bg-primary-light dark:bg-primary-dark text-white font-medium"
                    onClick={handleSaveProfile}
                    disabled={!fullName || !city || !country}
                  >
                    Save Profile
                  </button>
                </div>
              </div>
            )}
          </div>

          {/* Redesigned Statistics Section */}
          <div className="mt-8 relative">
            <div className="absolute inset-0 bg-gradient-to-r from-primary-light/10 to-primary-dark/10 dark:from-primary-light/5 dark:to-primary-dark/5 rounded-xl"></div>

            <div className="relative grid grid-cols-2 md:grid-cols-4 gap-0 divide-x divide-gray-200 dark:divide-gray-700 rounded-xl overflow-hidden">
              {/* Streak */}
              <div className="p-6 flex flex-col items-center justify-center transition-all hover:bg-white/50 dark:hover:bg-gray-800/50">
                <div className="w-12 h-12 rounded-full bg-gradient-to-br from-amber-400 to-red-500 flex items-center justify-center mb-3 shadow-lg transform transition-transform hover:scale-110">
                  <FiFlag className="text-white text-xl" />
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold mb-1">{user.streak}</div>
                  <div className="text-sm text-gray-600 dark:text-gray-400 font-medium">Day Streak</div>
                </div>
              </div>

              {/* Achievements */}
              <div className="p-6 flex flex-col items-center justify-center transition-all hover:bg-white/50 dark:hover:bg-gray-800/50">
                <div className="w-12 h-12 rounded-full bg-gradient-to-br from-blue-400 to-indigo-600 flex items-center justify-center mb-3 shadow-lg transform transition-transform hover:scale-110">
                  <FiAward className="text-white text-xl" />
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold mb-1">{user.achievements}</div>
                  <div className="text-sm text-gray-600 dark:text-gray-400 font-medium">Achievements</div>
                </div>
              </div>

              {/* Member Since */}
              <div className="p-6 flex flex-col items-center justify-center transition-all hover:bg-white/50 dark:hover:bg-gray-800/50">
                <div className="w-12 h-12 rounded-full bg-gradient-to-br from-emerald-400 to-green-600 flex items-center justify-center mb-3 shadow-lg transform transition-transform hover:scale-110">
                  <FiCalendar className="text-white text-xl" />
                </div>
                <div className="text-center">
                  <div className="text-base font-bold mb-1">
                    {new Date(user.joinDate).toLocaleDateString('en-US', { 
                      month: 'short', 
                      day: 'numeric', 
                      year: '2-digit' 
                    })}
                  </div>
                  <div className="text-sm text-gray-600 dark:text-gray-400 font-medium">Member Since</div>
                </div>
              </div>

              {/* Tasks Completed */}
              <div className="p-6 flex flex-col items-center justify-center transition-all hover:bg-white/50 dark:hover:bg-gray-800/50">
                <div className="w-12 h-12 rounded-full bg-gradient-to-br from-rose-400 to-pink-600 flex items-center justify-center mb-3 shadow-lg transform transition-transform hover:scale-110">
                  <FiCheckCircle className="text-white text-xl" />
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold mb-1">{user.tasksCompleted}</div>
                  <div className="text-sm text-gray-600 dark:text-gray-400 font-medium">Tasks Completed</div>
                </div>
              </div>
            </div>

            {/* Achievement Progress */}
            {user.tasksCompleted > 0 && (
              <div className="mt-6 p-4 bg-white dark:bg-gray-800 rounded-lg shadow-sm">
                <div className="flex justify-between items-center mb-2">
                  <h3 className="text-sm font-medium">Next Achievement Progress</h3>
                  <span className="text-xs text-primary-light dark:text-primary-dark font-medium">
                    Level {user.achievements + 1}
                  </span>
                </div>

                {/* Calculate next achievement threshold */}
                {(() => {
                  const nextThreshold = 5 * Math.pow(2, user.achievements);
                  const progress = Math.min(100, (user.tasksCompleted / nextThreshold) * 100);

                  return (
                    <>
                      <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                        <div 
                          className="bg-primary-light dark:bg-primary-dark h-2 rounded-full transition-all duration-500 ease-out"
                          style={{ width: `${progress}%` }}
                        ></div>
                      </div>
                      <div className="flex justify-between mt-1">
                        <span className="text-xs text-gray-500 dark:text-gray-400">
                          {user.tasksCompleted} tasks
                        </span>
                        <span className="text-xs text-gray-500 dark:text-gray-400">
                          {nextThreshold} tasks
                        </span>
                      </div>
                    </>
                  );
                })()}
              </div>
            )}
          </div>
        </div>

        {/* Settings */}
        <div className="card">
          <h2 className="text-xl font-bold mb-6">Preferences</h2>

          {/* Theme Mode Toggle */}
          <div className="mb-8">
            <div className="flex items-center justify-between mb-3">
              <span className="font-medium">Theme Mode</span>
              <button 
                onClick={toggleMode}
                className="relative inline-flex h-6 w-11 items-center rounded-full transition-colors focus:outline-none focus:ring-2 focus:ring-primary-light focus:ring-offset-1 dark:focus:ring-primary-dark"
                style={{ backgroundColor: mode === 'dark' ? 'var(--color-primary-dark)' : '#D1D5DB' }}
              >
                <span
                  className={`${
                    mode === 'dark' ? 'translate-x-6 bg-gray-800' : 'translate-x-1 bg-white'
                  } inline-block h-4 w-4 transform rounded-full transition-transform`}
                />
                <span className="absolute inset-0 flex items-center justify-start pl-1.5">
                  {mode === 'dark' && <FiMoon className="text-white text-xs" />}
                </span>
                <span className="absolute inset-0 flex items-center justify-end pr-1.5">
                  {mode === 'light' && <FiSun className="text-gray-600 text-xs" />}
                </span>
              </button>
            </div>
            <p className="text-sm text-gray-600 dark:text-gray-400">
              {mode === 'light' ? 'Switch to dark mode to reduce eye strain at night' : 'Switch to light mode for a brighter interface'}
            </p>
          </div>

          {/* Theme Color */}
          <div className="mb-8">
            <h3 className="font-medium mb-3">Theme Color</h3>
            <div className="grid grid-cols-5 gap-3">
              {themeColors.map((color) => (
                <button
                  key={color}
                  onClick={() => setThemeColor(color)}
                  className={`relative w-full aspect-square rounded-full transition-transform hover:scale-110 ${
                    themeColor === color ? 'ring-2 ring-offset-2 ring-gray-400 dark:ring-gray-600 scale-110' : ''
                  }`}
                  style={{ 
                    backgroundColor: 
                      color === 'indigo' ? '#4f46e5' : 
                      color === 'emerald' ? '#10b981' : 
                      color === 'rose' ? '#e11d48' : 
                      color === 'amber' ? '#d97706' : 
                      '#2563eb'
                  }}
                >
                  {themeColor === color && (
                    <span className="absolute inset-0 flex items-center justify-center">
                      <span className="w-2 h-2 bg-white rounded-full"></span>
                    </span>
                  )}
                </button>
              ))}
            </div>
            <p className="text-sm text-gray-600 dark:text-gray-400 mt-2">
              Personalize your experience with your favorite color
            </p>
          </div>

          {/* Workweek Days */}
          <div className="mb-6">
            <h3 className="font-medium mb-3">Workweek Days</h3>
            <div className="flex flex-wrap gap-2">
              {weekdays.map((day, index) => {
                const dayNumber = index + 1;
                return (
                  <button
                    key={day}
                    onClick={() => toggleWorkday(dayNumber)}
                    className={`relative w-10 h-10 rounded-full flex items-center justify-center transition-all ${
                      user.workweekDays.includes(dayNumber)
                        ? 'bg-primary-light dark:bg-primary-dark text-white shadow-md'
                        : 'bg-gray-100 dark:bg-gray-700 text-gray-700 dark:text-gray-300'
                    }`}
                  >
                    {day.charAt(0)}
                  </button>
                );
              })}
            </div>
            <p className="text-sm text-gray-600 dark:text-gray-400 mt-2">
              Select the days you typically work on tasks
            </p>
          </div>

          {/* Help & Tour */}
          <div className="pt-4 border-t dark:border-gray-700">
            <button 
              onClick={startTour}
              className="w-full py-3 flex items-center justify-center space-x-2 text-primary-light dark:text-primary-dark font-medium hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors"
            >
              <FiHelpCircle />
              <span>Start App Tour</span>
            </button>

            <button 
              onClick={logout}
              className="w-full mt-3 py-3 flex items-center justify-center space-x-2 text-rose-600 dark:text-rose-400 font-medium hover:bg-rose-50 dark:hover:bg-rose-900/20 rounded-lg transition-colors"
            >
              <FiLogOut />
              <span>Log Out</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
