        id: '2',
        title: 'Grocery shopping',
        description: 'Buy fruits, vegetables, and other essentials',
        dueDate: new Date(Date.now() + 86400000).toISOString().split('T')[0],
        category: 'Personal',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'medium',
        days: [2, 5]
      },
      {
        id: '3',
        title: 'Read a book',
        description: 'Continue reading "Atomic Habits"',
        dueDate: new Date(Date.now() + 172800000).toISOString().split('T')[0],
        category: 'Personal',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'low',
        days: [1, 2, 3, 4, 5, 6, 7]
      },
      {
        id: '4',
        title: 'Meditation session',
        description: '15 minutes of mindfulness meditation',
        dueDate: new Date().toISOString().split('T')[0],
        category: 'Personal',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'medium',
        days: [1, 2, 3, 4, 5, 6, 7]
      },
      
      // Work tasks
      {
        id: '5',
        title: 'Weekly team meeting',
        description: 'Discuss project progress and roadblocks',
        dueDate: new Date(Date.now() + 86400000).toISOString().split('T')[0],
        category: 'Work',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'high',
        days: [2]
      },
      {
        id: '6',
        title: 'Complete project proposal',
        description: 'Finalize the budget and timeline',
        dueDate: new Date(Date.now() + 259200000).toISOString().split('T')[0],
        category: 'Work',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'high',
        days: [1, 2, 3, 4, 5]
      },
      {
        id: '7',
        title: 'Review code changes',
        description: 'Check pull requests from the development team',
        dueDate: new Date().toISOString().split('T')[0],
        category: 'Work',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'medium',
        days: [1, 3, 5]
      },
      {
        id: '8',
        title: 'Update documentation',
        description: 'Add new features to the user guide',
        dueDate: new Date(Date.now() + 172800000).toISOString().split('T')[0],
        category: 'Work',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'low',
        days: [4]
      },
      
      // Home tasks
      {
        id: '9',
        title: 'Clean the kitchen',
