        dueDate: new Date(Date.now() + 172800000).toISOString().split('T')[0],
        category: 'Friends',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'medium',
        days: [6]
      },
      
      // Family tasks
      {
        id: '17',
        title: 'Family dinner',
        description: 'Cook special meal for everyone',
        dueDate: new Date(Date.now() + 86400000).toISOString().split('T')[0],
        category: 'Family',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'high',
        days: [7]
      },
      {
        id: '18',
