import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';

type ThemeMode = 'light' | 'dark';
type ThemeColor = 'indigo' | 'emerald' | 'rose' | 'amber' | 'blue';

interface ThemeContextType {
  mode: ThemeMode;
  toggleMode: () => void;
  themeColor: ThemeColor;
  setThemeColor: (color: ThemeColor) => void;
  applyThemeColor: (color: ThemeColor) => void;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

interface ThemeProviderProps {
  children: ReactNode;
}

const colorMap = {
  indigo: {
    light: '#4f46e5',
    dark: '#6366f1',
  },
  emerald: {
    light: '#10b981',
    dark: '#34d399',
  },
  rose: {
    light: '#e11d48',
    dark: '#fb7185',
  },
  amber: {
    light: '#d97706',
    dark: '#f59e0b',
  },
  blue: {
    light: '#2563eb',
    dark: '#3b82f6',
  },
};

export const ThemeProvider: React.FC<ThemeProviderProps> = ({ children }) => {
  const [mode, setMode] = useState<ThemeMode>(() => {
    const savedMode = localStorage.getItem('theme-mode');
    return (savedMode as ThemeMode) || (window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light');
  });
  
  const [themeColor, setThemeColor] = useState<ThemeColor>(() => {
    const savedColor = localStorage.getItem('theme-color');
    return (savedColor as ThemeColor) || 'indigo';
  });

  useEffect(() => {
    localStorage.setItem('theme-mode', mode);
    if (mode === 'dark') {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [mode]);

  useEffect(() => {
    localStorage.setItem('theme-color', themeColor);
    applyThemeColor(themeColor);
  }, [themeColor]);

  const toggleMode = () => {
    setMode(prevMode => prevMode === 'light' ? 'dark' : 'light');
  };

  const applyThemeColor = (color: ThemeColor) => {
    document.documentElement.style.setProperty('--color-primary-light', colorMap[color].light);
    document.documentElement.style.setProperty('--color-primary-dark', colorMap[color].dark);
  };

  return (
    <ThemeContext.Provider value={{ mode, toggleMode, themeColor, setThemeColor, applyThemeColor }}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = (): ThemeContextType => {
  const context = useContext(ThemeContext);
  if (context === undefined) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};
