import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { useAuth } from './AuthContext';

export type TaskCategory = 'Personal' | 'Work' | 'Home' | 'Friends' | 'Family';

export interface Task {
  id: string;
  title: string;
  description?: string;
  dueDate: string;
  category: TaskCategory;
  completed: boolean;
  createdAt: string;
  importance?: 'low' | 'medium' | 'high';
  days?: number[];
}

interface TaskContextType {
  tasks: Task[];
  addTask: (task: Omit<Task, 'id' | 'createdAt'>) => void;
  updateTask: (id: string, task: Partial<Task>) => void;
  deleteTask: (id: string) => void;
  completeTask: (id: string) => void;
  getTodayTasks: () => Task[];
  getUpcomingTasks: () => Task[];
  getTasksByCategory: (category: TaskCategory) => Task[];
  getTasksForNextDays: (days: number) => Record<string, Task[]>;
}

const TaskContext = createContext<TaskContextType | undefined>(undefined);

interface TaskProviderProps {
  children: ReactNode;
}

export const TaskProvider: React.FC<TaskProviderProps> = ({ children }) => {
  const { user, updateProfile } = useAuth();
  const [tasks, setTasks] = useState<Task[]>(() => {
    const savedTasks = localStorage.getItem('plannr_tasks');
    if (savedTasks) {
      return JSON.parse(savedTasks);
    }
    
    // Sample tasks
    const sampleTasks: Task[] = [
      // Personal tasks
      {
        id: '1',
        title: 'Morning workout routine',
        description: '30 minutes of cardio and strength training',
        dueDate: new Date().toISOString().split('T')[0],
        category: 'Personal',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'high',
        days: [1, 3, 5]
      },
      {
        id: '2',
        title: 'Grocery shopping',
        description: 'Buy fruits, vegetables, and other essentials',
        dueDate: new Date(Date.now() + 86400000).toISOString().split('T')[0],
        category: 'Personal',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'medium',
        days: [2, 5]
      },
      {
        id: '3',
        title: 'Read a book',
        description: 'Continue reading "Atomic Habits"',
        dueDate: new Date(Date.now() + 172800000).toISOString().split('T')[0],
        category: 'Personal',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'low',
        days: [1, 2, 3, 4, 5, 6, 7]
      },
      {
        id: '4',
        title: 'Meditation session',
        description: '15 minutes of mindfulness meditation',
        dueDate: new Date().toISOString().split('T')[0],
        category: 'Personal',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'medium',
        days: [1, 2, 3, 4, 5, 6, 7]
      },
      
      // Work tasks
      {
        id: '5',
        title: 'Weekly team meeting',
        description: 'Discuss project progress and roadblocks',
        dueDate: new Date(Date.now() + 86400000).toISOString().split('T')[0],
        category: 'Work',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'high',
        days: [2]
      },
      {
        id: '6',
        title: 'Complete project proposal',
        description: 'Finalize the budget and timeline',
        dueDate: new Date(Date.now() + 259200000).toISOString().split('T')[0],
        category: 'Work',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'high',
        days: [1, 2, 3, 4, 5]
      },
      {
        id: '7',
        title: 'Review code changes',
        description: 'Check pull requests from the development team',
        dueDate: new Date().toISOString().split('T')[0],
        category: 'Work',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'medium',
        days: [1, 3, 5]
      },
      {
        id: '8',
        title: 'Update documentation',
        description: 'Add new features to the user guide',
        dueDate: new Date(Date.now() + 172800000).toISOString().split('T')[0],
        category: 'Work',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'low',
        days: [4]
      },
      
      // Home tasks
      {
        id: '9',
        title: 'Clean the kitchen',
        description: 'Wash dishes and wipe counters',
        dueDate: new Date().toISOString().split('T')[0],
        category: 'Home',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'medium',
        days: [1, 4, 7]
      },
      {
        id: '10',
        title: 'Do laundry',
        description: 'Wash, dry, and fold clothes',
        dueDate: new Date(Date.now() + 86400000).toISOString().split('T')[0],
        category: 'Home',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'medium',
        days: [6]
      },
      {
        id: '11',
        title: 'Pay utility bills',
        description: 'Electricity, water, and internet',
        dueDate: new Date(Date.now() + 345600000).toISOString().split('T')[0],
        category: 'Home',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'high',
        days: [5]
      },
      {
        id: '12',
        title: 'Organize closet',
        description: 'Sort clothes and donate unused items',
        dueDate: new Date(Date.now() + 259200000).toISOString().split('T')[0],
        category: 'Home',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'low',
        days: [7]
      },
      
      // Friends tasks
      {
        id: '13',
        title: 'Call Sarah',
        description: 'Catch up and plan weekend meetup',
        dueDate: new Date().toISOString().split('T')[0],
        category: 'Friends',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'medium',
        days: [3]
      },
      {
        id: '14',
        title: 'Plan birthday party',
        description: 'Organize surprise party for Alex',
        dueDate: new Date(Date.now() + 604800000).toISOString().split('T')[0],
        category: 'Friends',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'high',
        days: [2, 4]
      },
      {
        id: '15',
        title: 'Movie night',
        description: 'Watch new Marvel movie with friends',
        dueDate: new Date(Date.now() + 432000000).toISOString().split('T')[0],
        category: 'Friends',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'low',
        days: [5]
      },
      {
        id: '16',
        title: 'Coffee meetup',
        description: 'Meet with college friends at Starbucks',
        dueDate: new Date(Date.now() + 172800000).toISOString().split('T')[0],
        category: 'Friends',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'medium',
        days: [6]
      },
      
      // Family tasks
      {
        id: '17',
        title: 'Family dinner',
        description: 'Cook special meal for everyone',
        dueDate: new Date(Date.now() + 86400000).toISOString().split('T')[0],
        category: 'Family',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'high',
        days: [7]
      },
      {
        id: '18',
        title: 'Help mom with shopping',
        description: 'Drive mom to the mall',
        dueDate: new Date(Date.now() + 259200000).toISOString().split('T')[0],
        category: 'Family',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'medium',
        days: [6]
      },
      {
        id: '19',
        title: 'Call grandparents',
        description: 'Weekly check-in call',
        dueDate: new Date().toISOString().split('T')[0],
        category: 'Family',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'high',
        days: [7]
      },
      {
        id: '20',
        title: 'Plan family vacation',
        description: 'Research destinations and accommodations',
        dueDate: new Date(Date.now() + 1209600000).toISOString().split('T')[0],
        category: 'Family',
        completed: false,
        createdAt: new Date().toISOString(),
        importance: 'low',
        days: [1, 3]
      }
    ];
    
    return sampleTasks;
  });

  useEffect(() => {
    localStorage.setItem('plannr_tasks', JSON.stringify(tasks));
  }, [tasks]);

  const addTask = (task: Omit<Task, 'id' | 'createdAt'>) => {
    const newTask: Task = {
      ...task,
      id: Date.now().toString(),
      createdAt: new Date().toISOString(),
    };
    setTasks(prevTasks => [...prevTasks, newTask]);
  };

  const updateTask = (id: string, updatedTask: Partial<Task>) => {
    setTasks(prevTasks => 
      prevTasks.map(task => 
        task.id === id ? { ...task, ...updatedTask } : task
      )
    );
  };

  const deleteTask = (id: string) => {
    setTasks(prevTasks => prevTasks.filter(task => task.id !== id));
  };

  const completeTask = (id: string) => {
    setTasks(prevTasks => 
      prevTasks.map(task => 
        task.id === id ? { ...task, completed: true } : task
      )
    );
    
    // Update user stats
    if (user) {
      const completedTasks = user.tasksCompleted + 1;
      const achievements = Math.floor(completedTasks / 5);
      
      // Calculate streak
      const streak = user.streak + 1;
      
      updateProfile({
        tasksCompleted: completedTasks,
        achievements,
        streak,
      });
    }
  };

  const getTodayTasks = () => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    
    return tasks.filter(task => {
      const taskDate = new Date(task.dueDate);
      taskDate.setHours(0, 0, 0, 0);
      return taskDate.getTime() === today.getTime() && !task.completed;
    });
  };

  const getUpcomingTasks = () => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    
    return tasks.filter(task => {
      const taskDate = new Date(task.dueDate);
      taskDate.setHours(0, 0, 0, 0);
      return taskDate.getTime() > today.getTime() && !task.completed;
    }).sort((a, b) => new Date(a.dueDate).getTime() - new Date(b.dueDate).getTime());
  };

  const getTasksByCategory = (category: TaskCategory) => {
    return tasks.filter(task => task.category === category && !task.completed);
  };

  const getTasksForNextDays = (days: number) => {
    const result: Record<string, Task[]> = {};
    const today = new Date();
    
    for (let i = 0; i < days; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() + i);
      date.setHours(0, 0, 0, 0);
      
      const dateStr = date.toISOString().split('T')[0];
      
      result[dateStr] = tasks.filter(task => {
        const taskDate = new Date(task.dueDate);
        taskDate.setHours(0, 0, 0, 0);
        return taskDate.getTime() === date.getTime() && !task.completed;
      });
    }
    
    return result;
  };

  return (
    <TaskContext.Provider value={{
      tasks,
      addTask,
      updateTask,
      deleteTask,
      completeTask,
      getTodayTasks,
      getUpcomingTasks,
      getTasksByCategory,
      getTasksForNextDays,
    }}>
      {children}
    </TaskContext.Provider>
  );
};

export const useTask = (): TaskContextType => {
  const context = useContext(TaskContext);
  if (context === undefined) {
    throw new Error('useTask must be used within a TaskProvider');
  }
  return context;
};
